import React from 'react' 
import Dog from './dog'
import { connect } from 'react-redux'
import { addDogs } from '../actions/addDogs'

class DogsList extends React.Component {
    // state = {
    //     dogs: []
    // }
    componentDidMount(){
        fetch('http://localhost:3000/pups')
            .then(resp => resp.json())
            .then(dogs => this.props.dispatch(addDogs(dogs)))
    }
    renderDogs = () => {
        return this.props.dogs.map(dog => <Dog key={dog.id} dog={dog} />)
    }

    render() {
        // console.log(this.props.dogs)
        return(
            <React.Fragment>
                {this.renderDogs()}
            </React.Fragment>
        )
    }
}
const mSTP = state => {
    return {
        dogs: state.dogs
    }
}
const mDTP = dispatch => {
    return {
        addDogs: dogs => dispatch(addDogs(dogs))
    }
}

export default connect(mSTP)(DogsList)
