import React from 'react' 

class Dog extends React.Component {
    state = {
        dogs: []
    }
    componentDidMount(){
        fetch('http://localhost:3000/pups')
            .then(resp => resp.json())
            .then(dogs => this.setState({dogs}))
    }
    renderDogs = () => {
        return this.state.dogs.map(dog => <h1>{dog.name}</h1>)
    }

    render() {
        console.log(this.state.dogs)
        return(
            <div>
                {this.renderDogs}
            </div>
        )
    }
}

export default Dog
