import React from 'react' 
import { connect } from 'react-redux'
import { addDog } from '../actions/addDogs'

class Dog extends React.Component {
    handleClick = (e) => {
        // this.props.addDog(this.props.dog)

    }
    render() {
        return (
            <React.Fragment >
                <span onClick={this.props.addDog(this.props.dog)}>{this.props.dog.name}</span>
            </React.Fragment>
        )
    }
}
const mDTP = dispatch => {
    return {
        addDog: dog => dispatch(addDog(dog))
    }
}
export default connect(null, mDTP)(Dog)
